function [h] = RA225295_L03_03()  %Essa funcao retorna o valor de h para qual o equilibrio entre  %Peso da esfera e empuxo seja alcan√ßado  %A formula do vulume fora da agua foi usada para subtrair do volume total  %da esfera que nos retornara um volume submerso  % com isso, basta colularmos Empuxo-Peso e encontrarmos o ponto de equilibrio  % com E-P-0  g=9.81; %m/s^2  r=1; %m  rhoe=200;   %kg/m^3  rhoa=1000;  %kg/m^3  Vesfera=(4/3)*(pi*r^3); %m^3  Vacima=@(h) ((pi*(h*h))/(3))*(3*r-h);%m^3  Vabaixo=@(h) (Vesfera-Vacima(h));%m^3  E=@(h) Vabaixo(h)*rhoa*g; %N  P=Vesfera*rhoe*g;         %N  f=@(h) E(h)-P; %0=E-P  h=fzero(f,[0 2]);   %mend